import React, { PureComponent } from 'react';
import Breadcrumbs from '@trendmicro/react-breadcrumbs';
import { Button, ButtonGroup } from '@trendimcro/react-buttons';
import Dropdown, { MenuItem } from '@trendmicro/react-dropdown';
import styled from 'styled-components';
import { Link, BrowserRouter as Router } from 'react-router-dom';
import { slide as Menu } from 'react-burger-menu';
import '../styles/css/SideBar.scss';

class SideBar extends React.PureComponent {
  render() {
    return (
      <div
        style={{
          marginLeft: expanded ? 240 : 64,
          padding: '15px 20px 0 20px',
        }}
      >
        <ButtonGroup>
          <Button btnStyle="flat" onClick={this.navigate('home')}>
            Home
          </Button>
          <Button btnStyle="flat" onClick={this.navigate('devices')}>
            Devices
          </Button>
          <Button btnStyle="flat" onClick={this.navigate('reports')}>
            Reports
          </Button>
          <Dropdown>
            <Dropdown.Toggle>Settings</Dropdown.Toggle>
            <Dropdown.Menu>
              <MenuItem onClick={this.navigate('settings/policies')}>
                Policies
              </MenuItem>
              <MenuItem onClick={this.navigate('settings/network')}>
                Network
              </MenuItem>
            </Dropdown.Menu>
          </Dropdown>
        </ButtonGroup>
      </div>
    );
  }
}

export default SideBar;
